let Nspecies = 20 let Nsamples = 2000
type W = { Topt: double[]
Tbreadth: double[] MaxProb: double[] Terr: double Yerr: double Ttrue: double[]}
type Y = {Tobs: double[]; Y: double[][]}
let CalcSpProb w t sp = let z = (t − w.Topt.[sp]) / w.Tbreadth.[sp] w.MaxProb.[sp] ∗ exp (− z∗z)
[<Fun>] let prior () = { Topt = [| for j in 0..Nspecies−1→random(Uniform(0.1, 50.0)) |]
Tbreadth = [| for j in 0..Nspecies−1→random(Uniform(0.1, 50.0)) |] MaxProb = [| for j in 0..Nspecies−1→random(Uniform(0.1, 1.0)) |] Terr = random(Uniform(0.1, 10.0)) Yerr = random(Uniform(0.01, 0.5))
Ttrue = [| for i in 0..Nsamples−1→random(Uniform(5.0, 30.0))|] }
let samplesR = [|0..Nsamples−1|] let speciesR = [|0..Nspecies−1|]
[<Fun>] let model w =
let tobs = [| for i in samplesR→random(Gaussian(w.Ttrue.[i], w.Terr)) |] let y = [| for i in samplesR→
[| for j in speciesR→ let p = CalcSpProb w w.Ttrue.[i] j random(Gaussian(p, w.Yerr)) |] |]
{ Tobs = tobs Y = y }
// the generated log probability density function for model let logPdf =
fun w ys→ let y = ys.Y let tobs = ys.Tobs let i0 = samplesR (logprodBy((fun i1→
let i=samplesR.[i1] Log(pdf Gaussian(w.Ttrue.[i],w.Terr,tobs.[i1]))),
samplesR.Length)) + (let i2 = samplesR logprodBy((fun i3→
let i=samplesR.[i3] let i4=speciesR logprodBy((fun i5→
let j=speciesR.[i5] Log(pdf Gaussian(CalcSpProb w (w.Ttrue.[i]) j,w.Yerr,y.[i3].[i5]))),
speciesR.Length)), samplesR.Length))
